                    $select_projekti_gradovi = "
                        SELECT naziv_grada FROM gradovi;
                    ";
                    $gradovi = $db_handle->runQuery($select_projekti_gradovi);

                    // print_r($gradovi);

                    $select_projekti_grad = "
                    SELECT *,
                    gradovi.naziv_grada AS grad,
                    procon_projekti.opis AS projekti_opis,
                    procon_projekti.id AS projekti_id,
                    procon_projekti_kategorija.kategorija AS naziv_kategorije
                    FROM projekat_gradovi
          
                    LEFT JOIN gradovi ON projekat_gradovi.grad=gradovi.id
                    LEFT JOIN procon_projekti ON projekat_gradovi.projekat=procon_projekti.id
                    LEFT JOIN procon_projekti_kategorija ON procon_projekti.kategorija=procon_projekti_kategorija.id

                    LEFT JOIN users AS ke_users ON procon_projekti.korisnik_edit=ke_users.id WHERE 1
                    ORDER BY procon_projekti.datum_unos DESC
                    ";
                    $projekti_query_gradovi = $db_handle->runQuery($select_projekti_grad);
                

                    // print_r($projekti_query_gradovi);

                    foreach($gradovi as &$grad_value) {

                        // echo $grad_value['grad'];
                        
                        echo '<div id="opstina'.$grad_value['naziv_grada'].'" data-name="'.$grad_value['naziv_grada'].'" style="display:none" class="opstina_projekti">
                            <h2 class="opstinaNaslov">'.$grad_value['naziv_grada'].'</h2>';
                        
                            foreach ($projekti_query_gradovi as $key => &$value) {
                                $id = $projekti_query_gradovi[$key]["projekti_id"];
                                $title = $projekti_query_gradovi[$key]["naslov"];
                                $grad = $projekti_query_gradovi[$key]['grad'];
                                $naziv_kategorije = $projekti_query_gradovi[$key]["naziv_kategorije"];
                                $kategorija = '';
                                // $title = substr($title, 0, 30) .' . . .';

                                switch($naziv_kategorije) {
                                    case "Otpadne vode":
                                        $kategorija = 'otpadne-vode';
                                        break;
                                    case "Vodosnabdijevanje":
                                        $kategorija = 'vodosnabdijevanje';
                                        break;
                                    case "Čvrsti otpad":
                                        $kategorija = 'cvrsti-otpad';
                                        break;
                                    case "Socijalna infrastruktura":
                                        $kategorija = 'socijalna-infrastruktura';
                                        break;
                                }

                                if($grad_value['naziv_grada'] == $grad) {
                                    echo' <a href="projekti-template.php?id='.$id.'" target="_blank">
                                                <div class="opstinaProjekat" data-filter="#'.$kategorija.'">
                                                    <span class="naslov-projekta-mapa">'.$title.'</span>
                                                    <i class="fa-solid fa-angle-right"></i>
                                                </div>
                                            </a>
                                        ';
                                }

                            }

            echo '</div>';
                    }
